package Operator;

public class BitWiseOpearor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a,b,c,d,e,f;
		a=5;
		b=6;
		c=a&b;
		d=a|b;
		e=a<<2;
		f=b>>1;
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);

	}

}
